import React, { useState, useEffect } from 'react';
import './Movie.css'; 

function Watched() {
    const [movies, setMovies] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    throw new Error('No token found');
                }
                const response = await fetch('https://backend-g7rx.onrender.com/user/movies', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setMovies(data.movies || []);
            } catch (error) {
                console.error('Error fetching movies:', error);
                setError(error.message);
            }
        };
        fetchMovies();
    }, []);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="movie-container">
            {movies.length === 0 ? (
                <p>No movies found</p>
            ) : (
                movies.map((movie) => (
                    <div key={movie.movie_id} className="movie-item-watched">
                        <img
                            src={movie.poster || 'https://via.placeholder.com/150x225'}
                            alt={movie.title}
                        />
                        <h3>{movie.title}</h3>
                        <p>Year: {movie.prod_year}</p>
                        <p>Rating: {movie.user_rating || 'Not rated'}</p>
                        <p>Watched: {movie.watched_date ? new Date(movie.watched_date).toLocaleDateString() : 'N/A'}</p>
                    </div>
                ))
            )}
        </div>
    );
}

export default Watched;
