import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './App.css';

function Login() {
    // Stan dla pól formularza i komunikatów
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // Obsługa zmian w polach formularza
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    // Obsługa wysyłania formularza
    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        const payload = {
            username: formData.username,
            password: formData.password
        };

        try {
            const response = await fetch('http://127.0.0.1:5000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(payload)
            });

            const data = await response.json();

            if (response.ok) {
                // Zapisz token w localStorage
                localStorage.setItem('token', data.token);
                localStorage.setItem('user_id', data.user_id);
                setMessage('Logowanie zakończone sukcesem!');
                // Przekieruj na stronę główną po 1 sekundzie
                setTimeout(() => {
                    navigate('/home');
                }, 1000);
            } else {
                setError(data.error || 'Błąd logowania');
            }
        } catch (err) {
            setError('Błąd połączenia z serwerem');
        }
    };

    return (
        <div className="login-page">
            <div className="center">
                <h1 className="formTitle">Logowanie</h1>
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="username"
                        className="login"
                        placeholder="Login"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="password"
                        name="password"
                        className="password"
                        placeholder="Hasło"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="submit"
                        className="submitButtonLogin"
                        value="Zaloguj"
                    />
                </form>
                <p className="linkreg">
                    Załóż konto: <Link to="/register">Rejestracja</Link>
                </p>
            </div>
        </div>
    );
}

export default Login;
