import React, { useEffect, useState } from 'react';

import './Movie.css';

const Movie = () => {
    const [movies, setMovies] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Please log in to view your watched movies');
                    return;
                }

                const response = await axios.get('http://127.0.0.1:5000/user/movies', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });

                setMovies(response.data.movies);
            } catch (err) {
                setError(err.response?.data?.error || 'Failed to fetch movies');
            }
        };

        fetchMovies();
    }, []);

    if (error) {
        return <div className="error">{error}</div>;
    }

    return (
        <div className="movie-container">
            <h2>Your Watched Movies</h2>
            {movies.length === 0 ? (
                <p>You haven't watched any movies yet.</p>
            ) : (
                <ul className="movie-list">
                    {movies.map((movie) => (
                        <li key={movie.id} className="movie-item">
                            <h3>{movie.title}</h3>
                            <p>Genre: {movie.genre}</p>
                            <p>{movie.description}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Movie;
