import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Movie = ({ searchQuery }) => {
    const [movies, setMovies] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Please log in to view your watched movies');
                    return;
                }

                const response = await axios.get('http://127.0.0.1:5000/user/movies', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });

                setMovies(response.data.movies);
            } catch (err) {
                setError(err.response?.data?.error || 'Failed to fetch movies');
            }
        };

        fetchMovies();
    }, []);

    const filteredMovies = movies.filter((Movie) =>
        Movie.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="watched-container">
            <h2 className="header_text_watched">Your Watched Movies</h2>
            {filteredMovies.length === 0 ? (
                <p>You haven't watched any movies yet.</p>
            ) : (
                <div className="movie-list-watched">
                    {filteredMovies.map((movie) => (
                        <div key={movie.id} className="movie-item-watched">
                            <div className="movie-poster-container">
                                <img
                                    src={movie.poster || 'https://via.placeholder.com/150x225'}
                                    alt={movie.title}
                                    className="movie-poster-watched"
                                />
                            </div>
                            <div className="movie-details-watched">
                                <h2>{movie.title}</h2>
                                <p>Genre: {movie.genre}</p>
                                <p className="movie-description-watched">{movie.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Movie;
